mapper.select:
select g.gugun_code, g.gugun_name, g.sido_code from gugun g join sido s
on g.sido_code = s.sido_code
where g.sido_code = #{sidoCode}

mapper.update:
update attraction_review
attraction_review_score = #{attractionReviewScore}, attraction_review_content= #{attractionReviewContent},
where attraction_review_no = #{attractionReviewNo}

mapper.select:
SELECT * FROM attraction_info
WHERE 1=1

	AND sido_code = #{sido}


	AND gugun_code = #{gugun}


	AND content_type_id = #{type}


	AND title LIKE CONCAT("%", #{keyword}, "%")
